function setCookie(kv:string) {
	const arr = kv.split("=");
	let name = arr[0],value = arr[1],days = 100;
    var expires = "";
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days*24*60*60*1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (value || "")  + expires + "; path=/";
}

